import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({
    name: "",
    account_number: "",
    password: ""
  });
  const navigate = useNavigate();

  function updateForm(value) {
    return setForm((prev) => ({ ...prev, ...value }));
  }

  async function onSubmit(e) {
    e.preventDefault();

    const userCredentials = { ...form };

    await fetch("http://localhost:3001/user/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(userCredentials),
    })
      .then((response) => response.json())
      .then((data) => {
        localStorage.setItem("jwt", data.token);
        setForm({ name: "", account_number: "", password: "" });
        navigate("/pay");
      })
      .catch((error) => window.alert("Failed to log in: " + error.message));
  }

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow-sm">
            <div className="card-body">
              <h3 className="text-center">Login</h3>
              <form onSubmit={onSubmit}>
                <div className="form-group">
                  <label htmlFor="name">Username</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    value={form.name}
                    onChange={(e) => updateForm({ name: e.target.value })}
                    placeholder="Username"
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="account_number">Account Number</label>
                  <input
                    type="text"
                    className="form-control"
                    id="account_number"
                    value={form.account_number}
                    onChange={(e) => updateForm({ account_number: e.target.value })}
                    placeholder="Account Number"
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={form.password}
                    onChange={(e) => updateForm({ password: e.target.value })}
                    placeholder="Password"
                    required
                  />
                </div>
                <div className="text-center">
                  <input type="submit" value="Login" className="btn btn-primary btn-block" />
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
